import groovy.json.JsonSlurper

//grab the response
def ResponseMessage = messageExchange.response.responseContent
//define a JsonSlurper
def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)

//verify the slurper isn't empty
assert !(jsonSlurper.isEmpty())

//verify CODE
assert jsonSlurper.Name[0]=="College"
assert jsonSlurper.Name[1]=="Corporate"
assert jsonSlurper.Name[2]=="Military"
assert jsonSlurper.Name[3]=="MOOC"
assert jsonSlurper.Name[4]=="Other"

import groovy.json.JsonSlurper

//grab the response
def ResponseMessage = messageExchange.response.responseContent
//define a JsonSlurper
def jsonSlurper = new JsonSlurper().parseText(ResponseMessage)

//verify the slurper isn't empty
assert !(jsonSlurper.isEmpty())

//verify CODE
assert jsonSlurper.Code[0]=="AC      "
assert jsonSlurper.Code[1]=="CC      "
assert jsonSlurper.Code[2]=="EC      "
assert jsonSlurper.Code[3]=="TC      "
